

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10;
		boolean a = true;
		boolean b = false;
		System.out.println(x++);
		System.out.println(++x);
		System.out.println(x--);
		System.out.println(--x);
		System.out.println(!a);
		System.out.println(!b);
		

	}

}
